import { Component, Input, Output, EventEmitter} from '@angular/core';

@Component({
  selector: 'app-lista-tarefas',
  templateUrl: './lista-tarefas.component.html',
  styleUrls: ['./lista-tarefas.component.css']
})
export class ListaTarefasComponent {


  @Input() listaTarefas_listaTarefasComponent: string[] = [];


  //@Output() tarefaAdicionada = new EventEmitter<string>();
  //com alias tarefaFoiCriada
  @Output('tarefaFoiCriada') tarefaAdicionada = new EventEmitter<string>();

  addTarefa(todo: string) {
    todo = "*---->" + todo;
    console.log("metodo chamado no filho")
    this.tarefaAdicionada.emit(todo);
  }


 }

<div *ngFor="let tarefa of listaTarefas_listaTarefasComponent">
  <app-item-tarefas [itemTarefa]="tarefa"></app-item-tarefas>
</div>

<!-- local referencia #-->
<input #novaTarefa type="text">
<!-- pegou o value de local referencia #-->
<button (click)="addTarefa(novaTarefa.value)" #botaoAdicionaTarefa>Adiciona Tarefa</button>


<p appDestaque>Directiva destaque</p>

<h1 appDestaque>Vamos para a aula!!!</h1>

<h2 appDestaque>Teste appDestaque</h2>
